function welcome

%%
%%	WELCOME.M	Puts Up Box To Welcome User and Waits for Button Press
%%
%%



MP = get(gcf,'Position');
WFPos = [MP(1)+0.1*MP(3) MP(2)+0.2*MP(4) 0.8*MP(3) 0.6*MP(4)];
TextSpace = .05;

Header = 'Welcome to CSL DEC-style analysis program'

message = [	'WC is a set of matlab routines designed for performing whole-cell analysis';
		    'on files generated by Q-II program on the INDEC system.                   '];

[Nlines,junk] = size(message);

WF=figure(		'Name',	' ', ...
			'NumberTitle', 'off', ...
			'Color', [1 1 1], ...
			'Units', 'normal',...
			'Position', WFPos);			

Waxes=axes(		'Units', 'normal',...
			'Position',[.05 .25 .90 .70],...
			'Visible','on');
	
Wmsg=text(		.5, (.5+TextSpace*(Nlines+3)) , ...
			Header,...
			'FontSize',[12],...
			'Color',[0 0 0],...
			'HorizontalAlignment','center',...
			'VerticalAlignment','middle');

count = 1; 

while (count <=Nlines)

	Wmsg=text(	.066, (.5+TextSpace*((Nlines+2)-2*(count+1))), ...
			message(count,:),...
			'FontSize',[10],...
			'Color',[0 0 0],...
			'HorizontalAlignment','left',...
			'VerticalAlignment','middle');
	count=count+1;
end


Wbut=uicontrol(		WF,...
			'Style','pushbutton', ...
			'Units','normalized',...
			'Position',[.3 .08 .4 .1],...
			'Callback',['delete(gcf),newfile'],...
			'String','Press to Continue',...
			'HorizontalAlignment','center');

